mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM (SELECT BNO, BREF, BSTEP, BLEVEL,READCOUNT, SUBJECT, CONTENT, WRITER, REGDATE, IP, PASSWD FROM BOARD_LIST
WHERE ${items} LIKE '%'||#{text}||'%'
ORDER BY BREF DESC, BNO, BLEVEL) A )
WHERE BNO=#{bno}
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE BOARD_LIST SET SUBJECT=#{subject}, CONTENT=#{content}, WRITER=#{writer},
REGDATE=TO_CHAR(SYSDATE, 'YYYY/MM/DD(HH:MM:SS)'), PASSWD=#{passwd}, IP=#{ip}
WHERE BNO=#{bno}
